<?php
namespace Panzer;

class Handler
{
	public function __construct()
	{
		@set_error_handler(array($this, 'error'));
		@set_exception_handler(array($this, 'exception'));
	}

   /**
     * Error handler. Convert all errors to Exceptions by throwing an ErrorException.
     *
     * @param int $level  Error level
     * @param string $message  Error message
     * @param string $file  Filename the error was raised in
     * @param int $line  Line number in the file
     *
     * @return void
     */
    public function error($level, $message, $file, $line)
    {
        if (error_reporting() !== 0) {  // to keep the @ operator working
            throw new \ErrorException($message, 0, $level, $file, $line);
        }
    }

    /**
     * Exception handler.
     *
     * @param Exception $exception  The exception
     *
     * @return void
     */
    public function exception($exception)
    {
    	$response = new Response;

        $response->setStatusCode($exception->getCode());

        if (DI::get('config')->debug()) {

        	$body = "<!DOCTYPE html><html><head><title>" . $response->getStatusCode() . " " . $response->getStringStatusCode() . "</title>";
        	$body .= "<style type=\"text/css\">";
			$body .= "body{font-family: monospace, \"Courier New\", Courier; width: 95%; margin: auto;}";
			$body .= "pre{white-space: pre-wrap;}";
			$body .= "table{width: 100%; text-align: left; border-collapse: collapse; word-break: break-word;}";
			$body .= "table td, table th{padding: 6px 4px;}";
			$body .= "table tbody td{font-size: 15px; color: #323f4e; line-height: 20px;}";
			$body .= "table tr.strip{height: 15px;}";
			$body .= "table tfoot td{font-size: 15px;}";
			$body .= "</style>";
        	$body .= "</head><body><table><tbody>";
            $body .= "<tr class=\"strip\"><td><strong>HTTP Request:</strong> " . $_SERVER['SERVER_PROTOCOL'] . " " . $_SERVER['REQUEST_METHOD'] ." ". htmlentities($_SERVER['REQUEST_URI']) . "</td></tr>";
            $body .= "<tr class=\"strip\"><td><strong>HTTP Response status code:</strong> " . $response->getStatusCode() . " " . $response->getStringStatusCode() . "</td></tr>";
            $body .= "<tr class=\"strip\"><td><strong>Uncaught exception:</strong> " . get_class($exception) . "</td></tr>";
            $body .= "<tr class=\"strip\"><td><strong>Message:</strong> '" . htmlentities($exception->getMessage()) . "'</td></tr>";
            $body .= "<tr class=\"strip\"><td><strong>Execution time:</strong> " . DI::get('kernel')->getExecutionTime() . "</td></tr>";
            $body .= "<tr><td><strong>Stack trace:</strong><pre>" . preg_replace('/^(#0.*)/', '<span style="color: red;">$1</span>', $exception->getTraceAsString()) . "</pre></td></tr>";
            $body .= "<tr class=\"strip\"><td>Thrown in '<u>" . $exception->getFile() . "</u>' on <u>line " . $exception->getLine() . "</u></td></tr>";
            $body .= "<tr><td><strong>File content:</strong><pre>" . $this->getSource($exception->getFile(), $exception->getLine()) . "</pre></td></tr>";
            $body .= "</tbody></table></body></html>";

            $assigned = [
            	$response->getStatusCode(),
            	$response->getStringStatusCode(),
            	$_SERVER['SERVER_PROTOCOL'],
            	$_SERVER['REQUEST_METHOD'],
            	htmlentities($_SERVER['REQUEST_URI']),
            	get_class($exception),
            	nl2br(htmlentities($exception->getMessage())),
            	DI::get('kernel')->getExecutionTime(),
             	preg_replace('/^(#0.*)/','<span style="color: red;">$1</span>', $exception->getTraceAsString()),
             	$exception->getFile(),
				$exception->getLine(),
 				$this->getSource($exception->getFile(), $exception->getLine())
 			];

            return $response->setBodyRaw($this->render($assigned), 'html')->send();
        } else {
        	if ($response->getStatusCode() == 500) {
	            ini_set("display_errors", 1);
	            ini_set("log_errors", 1);
	            ini_set('error_log', '../App/Logs/' . date('Y-m-d') . '.txt');

	            $log =  $_SERVER['SERVER_PROTOCOL'] . " " . $_SERVER['REQUEST_METHOD'] ." '". $_SERVER['REQUEST_URI']."'";
	            $log .= "\nUncaught exception: '" . get_class($exception) . "' with message '" . $exception->getMessage() . "' and code '" . $exception->getCode()."'";
	            $log .= "\nStack trace:\n" . $exception->getTraceAsString();
	            $log .= "\nThrown in '" . $exception->getFile() . "' on line " . $exception->getLine() . "\n";

	            error_log($log);
        	}

            $view = new View;
            $view->assign('e', $exception);
            $view->assign('r', $response);
			$view->renderError($response->getStatusCode());
        }
    }

    private function getSource($path, $offset)
    {
    	$lines = file($path);
    	return htmlentities(implode("", (array_slice($lines, $offset-21, 21, true))));
    }

